import React, { Component } from "react";
import { useSelector, useDispatch } from 'react-redux'
import getTemperature from '../actions/temperatureActions'

const TemperatureDisplay = props => {

    useEffect(() => {
        useDispatch(getTemperature())
    }, []) 

    const currentTemperature = useSelector(state => state.weatherReducer.currentTemperature)

    return(
        <div>
            Current temperature at gløshaugen: {currentTemperature} °C
        </div>
    )
}

export default TemperatureDisplay
